cle=18
texte =""

def decodeCesar(texte:str,cle:int)-> str:
    messDecode=""
    for i in range(len(texte)):
        if texte[i]==' ':
            messDecode+=' '
        else:
            asc=ord(texte[i])-cle
            messDecode+=chr(asc+26*((asc<65)-(asc>90)))
    return messDecode